import React, { useRef, useEffect } from 'react';

const ZenWaterCanvas = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let width = canvas.width = window.innerWidth;
    let height = canvas.height = 400;
    let mouse = { x: width / 2, y: height / 2 };
    let t = 0;

    function draw() {
      ctx.clearRect(0, 0, width, height);
      // Zen blue gradient background
      let grad = ctx.createLinearGradient(0, 0, width, height);
      grad.addColorStop(0, '#1a237e');
      grad.addColorStop(1, '#1976d2');
      ctx.fillStyle = grad;
      ctx.fillRect(0, 0, width, height);

      // Draw concentric sine waves
      for (let r = 0; r < 6; r++) {
        let radius = 60 + r * 30 + Math.sin(t / 20 + r) * 8;
        ctx.beginPath();
        ctx.arc(mouse.x, mouse.y, radius, 0, 2 * Math.PI);
        ctx.strokeStyle = `rgba(255,255,255,${0.08 + 0.04 * Math.cos(t / 30 + r)})`;
        ctx.lineWidth = 2 + Math.sin(t / 15 + r) * 0.5;
        ctx.stroke();
      }
      t++;
      requestAnimationFrame(draw);
    }

    draw();

    function onMove(e) {
      const rect = canvas.getBoundingClientRect();
      mouse.x = e.clientX - rect.left;
      mouse.y = e.clientY - rect.top;
    }
    canvas.addEventListener('mousemove', onMove);

    window.addEventListener('resize', () => {
      width = canvas.width = window.innerWidth;
      height = canvas.height = 400;
    });

    return () => {
      canvas.removeEventListener('mousemove', onMove);
    };
  }, []);

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: 400,
        zIndex: 0,
        pointerEvents: 'auto',
        display: 'block'
      }}
    />
  );
};

export default ZenWaterCanvas;
