import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import { Mail, ShoppingCart, Menu, X, Star, Heart, Search } from 'lucide-react';
import ProductCard from './components/ProductCard';
import CompatibilityModal from './components/CompatibilityModal';
import SiliconeInfoModal from './components/SiliconeInfoModal';
import { siteConfig } from './config/content';
import Footer from './components/Footer';
import ProductDetail from './components/ProductDetail';
import Feedback from './components/Feedback';

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [showSiliconeInfo, setShowSiliconeInfo] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState('all');

  // 首页和Products页面分类顺序统一
  const orderedCategories = [
    { id: 'bestSellers', name: siteConfig.categories.bestSellers.name },
    { id: 'classic', name: siteConfig.categories.classic.name },
    { id: 'accessories', name: siteConfig.categories.accessories.name },
  ];

  const products = [
    // Classic Series
    {
      id: 1,
      name: "Ice Molds for Owala 24-40oz, Set of 3 - Rose Pink",
      category: 'classic',
      color: "#ebcbbe",
      price: "$12.99",
      rating: 4.8,
      reviews: 156,
      description: "Designed specifically for Owala water bottles, our off-centered ice mold creates perfect long-lasting ice cubes. Made with premium food-grade silicone for safe and durable use.",
      features: [
        "Off center design, straw sits to one side",
        "Side wing design, easy to release",
        "Extended cooling duration",
        "Leak-proof seal",
      ],
      amazonLink: "https://www.amazon.com/Ice-Mold-Accessories-Placement-Compatible/dp/B0DHNTM3J7",
      images: [
        "/assets/images/products/pink-mold/1.jpg",
        "/assets/images/products/pink-mold/2.jpg",
        "/assets/images/products/pink-mold/3.jpg",
        "/assets/images/products/pink-mold/4.jpg",
        "/assets/images/products/pink-mold/5.jpg",
        "/assets/images/products/pink-mold/6.jpg",
        "/assets/images/products/pink-mold/7.jpg",
      ],
      compatibleBottles: [
        {
          name: "Owala Freesip",
          image:  "/assets/images/bottles/Owala_Freesip.png",
          description: "Perfect for 24oz, 32oz, 40oz wide mouth bottles"
        },
        {
          name: "Stanley Cup",
          image:  "/assets/images/bottles/Stanley_Cup.png",
          description: "Compatible with 30oz, 40oz bottles"
        }
      ]
    },
    {
      id: 2,
      name: "Ice Molds for Owala 24-40oz, Set of 3 - Sage Green",
      category: 'classic',
      color: "#a1cd9d",
      price: "$12.99",
      rating: 4.7,
      reviews: 142,
      description: "Designed specifically for Owala water bottles, our off-centered ice mold creates perfect long-lasting ice cubes. Made with premium food-grade silicone for safe and durable use.",
      features: [
        "Off center design, straw sits to one side",
        "Side wing design, easy to release",
        "Extended cooling duration",
        "Leak-proof seal"
      ],
      amazonLink: "https://www.amazon.com/Ice-Food-Grade-Silicone-Placement-Compatible/dp/B0DJ33LBHG",
      images: [
        "/assets/images/products/green-mold/1.jpg",
        "/assets/images/products/green-mold/2.jpg",
        "/assets/images/products/green-mold/3.jpg",
        "/assets/images/products/green-mold/4.jpg",
        "/assets/images/products/green-mold/5.jpg",
        "/assets/images/products/green-mold/6.jpg",
        "/assets/images/products/green-mold/7.jpg",
      ],
      compatibleBottles: [
        {
          name: "Owala Freesip",
          image:  "/assets/images/bottles/Owala_Freesip.png",
          description: "Perfect for 24oz, 32oz, 40oz wide mouth bottles"
        },
        {
          name: "Stanley Cup",
          image:  "/assets/images/bottles/Stanley_Cup.png",
          description: "Compatible with 30oz, 40oz bottles"
        }
      ]
    },
    // Limited Edition
    {
      id: 3,
      name: "Ice Molds for Owala 24-40oz, Set of 3 - Lavender Purple",
      category: 'limited',
      color: "#d4b5d8",
      price: "$14.99",
      rating: 4.9,
      reviews: 89,
      description: "Limited edition lavender purple with elegant design. Designed specifically for Owala water bottles, creating perfect long-lasting ice cubes.",
      features: [
        "Limited edition lavender purple",
        "Off center design, straw sits to one side",
        "Side wing design, easy to release",
        "Extended cooling duration",
        "Leak-proof seal"
      ],
      amazonLink: "#",
      images: [
        "/assets/images/products/pink-mold/1.jpg",
        "/assets/images/products/pink-mold/2.jpg",
        "/assets/images/products/pink-mold/3.jpg",
      ],
      compatibleBottles: [
        {
          name: "Owala Freesip",
          image:  "/assets/images/bottles/Owala_Freesip.png",
          description: "Perfect for 24oz, 32oz, 40oz wide mouth bottles"
        }
      ]
    },
    {
      id: 4,
      name: "Ice Molds for Owala 24-40oz, Set of 3 - Sunset Orange",
      category: 'limited',
      color: "#f4a261",
      price: "$14.99",
      rating: 4.8,
      reviews: 67,
      description: "Limited edition sunset orange with warm and vibrant design. Designed specifically for Owala water bottles, creating perfect long-lasting ice cubes.",
      features: [
        "Limited edition sunset orange",
        "Off center design, straw sits to one side",
        "Side wing design, easy to release",
        "Extended cooling duration",
        "Leak-proof seal"
      ],
      amazonLink: "#",
      images: [
        "/assets/images/products/green-mold/1.jpg",
        "/assets/images/products/green-mold/2.jpg",
        "/assets/images/products/green-mold/3.jpg",
      ],
      compatibleBottles: [
        {
          name: "Owala Freesip",
          image:  "/assets/images/bottles/Owala_Freesip.png",
          description: "Perfect for 24oz, 32oz, 40oz wide mouth bottles"
        }
      ]
    },
    // Travel Sets
    {
      id: 5,
      name: "Travel Ice Mold Set - Compact Edition",
      category: 'travel',
      color: "#74c0fc",
      price: "$18.99",
      rating: 4.6,
      reviews: 45,
      description: "Compact ice mold set designed for travel, includes portable storage case.",
      features: [
        "Compact design, space-saving",
        "Portable storage case",
        "Off center design, straw sits to one side",
        "Side wing design, easy to release",
        "Extended cooling duration"
      ],
      amazonLink: "#",
      images: [
        "/assets/images/products/pink-mold/1.jpg",
        "/assets/images/products/pink-mold/2.jpg",
      ],
      compatibleBottles: [
        {
          name: "Owala Freesip",
          image:  "/assets/images/bottles/Owala_Freesip.png",
          description: "Perfect for 24oz, 32oz, 40oz wide mouth bottles"
        }
      ]
    },
    {
      id: 6,
      name: "Travel Ice Mold Set - Premium Edition",
      category: 'travel',
      color: "#ffd43b",
      price: "$24.99",
      rating: 4.7,
      reviews: 38,
      description: "Premium travel set with multiple ice mold sizes and dedicated cleaning tools.",
      features: [
        "Multiple ice mold sizes",
        "Dedicated cleaning tools",
        "Premium storage case",
        "Off center design, straw sits to one side",
        "Side wing design, easy to release"
      ],
      amazonLink: "#",
      images: [
        "/assets/images/products/green-mold/1.jpg",
        "/assets/images/products/green-mold/2.jpg",
      ],
      compatibleBottles: [
        {
          name: "Owala Freesip",
          image:  "/assets/images/bottles/Owala_Freesip.png",
          description: "Perfect for 24oz, 32oz, 40oz wide mouth bottles"
        }
      ]
    },
    // Accessories
    {
      id: 7,
      name: "Silicone Cleaning Brush Set",
      category: 'accessories',
      color: "#adb5bd",
      price: "$7.99",
      rating: 4.5,
      reviews: 23,
      description: "Cleaning brush set designed specifically for silicone ice molds, gentle cleaning without damaging molds.",
      features: [
        "Gentle cleaning formula",
        "Non-damaging to silicone surface",
        "Multiple brush head sizes",
        "Portable design",
        "Food-grade materials"
      ],
      amazonLink: "#",
      images: [
        "/assets/images/products/pink-mold/1.jpg",
      ],
      compatibleBottles: []
    },
    {
      id: 8,
      name: "Ice Mold Storage Organizer",
      category: 'accessories',
      color: "#6c757d",
      price: "$6.99",
      rating: 4.4,
      reviews: 18,
      description: "Refrigerator-specific ice mold storage organizer, neat and space-saving.",
      features: [
        "Refrigerator-specific design",
        "Space-saving",
        "Non-slip base",
        "Transparent material",
        "Easy to clean"
      ],
      amazonLink: "#",
      images: [
        "/assets/images/products/green-mold/1.jpg",
      ],
      compatibleBottles: []
    },
    {
      id: 9,
      name: "Premium Silicone Ice Tray",
      category: 'accessories',
      color: "#495057",
      price: "$9.99",
      rating: 4.6,
      reviews: 31,
      description: "Upgraded traditional ice tray using food-grade silicone, easy to release.",
      features: [
        "Food-grade silicone",
        "Easy to release",
        "Non-slip base",
        "Multiple sizes",
        "Durable design"
      ],
      amazonLink: "#",
      images: [
        "/assets/images/products/pink-mold/1.jpg",
      ],
      compatibleBottles: []
    },
    {
      id: 10,
      name: "Ice Cube Dispenser",
      category: 'accessories',
      color: "#343a40",
      price: "$11.99",
      rating: 4.3,
      reviews: 15,
      description: "Smart ice cube dispenser, one-click release, avoiding hand contact.",
      features: [
        "One-click release design",
        "Avoid hand contact",
        "Large capacity storage",
        "Non-slip base",
        "Easy to clean"
      ],
      amazonLink: "#",
      images: [
        "/assets/images/products/green-mold/1.jpg",
      ],
      compatibleBottles: []
    }
  ];

  const filteredProducts =
    selectedCategory === 'all'
      ? products
      : selectedCategory === 'bestSellers'
        ? products.filter(p => p.category === 'classic').slice(0, 3)
        : products.filter(p => p.category === selectedCategory);

  const Home = () => (
    <div className="min-h-screen">
      {/* Hero Section */}
      <section className="relative bg-gradient-to-br from-brand-secondary to-brand-light py-32 overflow-hidden">
        {/* Background Image */}
        <div 
          className="absolute inset-0 bg-cover bg-center bg-no-repeat opacity-10"
          style={{
            backgroundImage: `url(${siteConfig.hero.backgroundImage})`
          }}
        ></div>

        {/* Background Pattern */}
        <div className="absolute inset-0 bg-gradient-to-br from-brand-primary/5 to-transparent"></div>

        <div className="relative max-w-6xl mx-auto px-4 text-center">
          <h1 className="text-6xl md:text-7xl font-light text-brand-primary mb-8 leading-tight">
            {siteConfig.hero.title}
          </h1>
          <p className="text-xl md:text-2xl text-brand-primary/80 mb-12 max-w-3xl mx-auto leading-relaxed">
            {siteConfig.hero.subtitle}
          </p>
          <button 
            onClick={() => setCurrentPage('products')}
            className="bg-brand-primary text-white px-10 py-4 rounded-full hover:bg-brand-primary/90 transition-all duration-300 text-lg font-medium shadow-lg hover:shadow-xl transform hover:-translate-y-1"
          >
            {siteConfig.hero.cta}
          </button>
        </div>
      </section>

      {/* Categories Section */}
      <section className="py-20 bg-white">
        <div className="max-w-6xl mx-auto px-4">
          <h2 className="text-4xl font-light text-brand-primary text-center mb-16">
            {siteConfig.categories.title}
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
            {orderedCategories.map(category => (
              <div 
                key={category.id}
                className="bg-brand-light rounded-xl p-8 text-center hover:shadow-xl transition-all duration-300 cursor-pointer transform hover:-translate-y-2"
                onClick={() => {
                  setSelectedCategory(category.id);
                  setCurrentPage('products');
                }}
              >
                <h3 className="text-2xl font-light text-brand-primary mb-4">
                  {category.name}
                </h3>
                <p className="text-brand-primary/70 leading-relaxed">
                  {siteConfig.categories[category.id].description}
                </p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Featured Products by Category */}
      <div className="w-full bg-white">
        {/* Best Sellers Section */}
        <section className="w-full">
          <div className="max-w-6xl mx-auto product-section">
            <div className="text-left mb-16">
              <h2 className="product-section-title">
                {siteConfig.categories.bestSellers.slogan}
              </h2>
              <p className="product-section-subtitle">
                {siteConfig.categories.bestSellers.description}
              </p>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
              {products.filter(p => p.category === 'classic').slice(0, 3).map(product => (
                <ProductCard 
                  key={product.id} 
                  product={product}
                  onShowCompatibility={setSelectedProduct}
                />
              ))}
            </div>
            <div className="text-center mt-12">
              <button 
                onClick={() => {
                  setSelectedCategory('classic');
                  setCurrentPage('products');
                }}
                className="text-brand-primary hover:text-brand-primary/80 text-lg font-medium underline"
              >
                View All Best Sellers →
              </button>
            </div>
          </div>
        </section>

        {/* Classic Series Section */}
        <section className="w-full">
          <div className="max-w-6xl mx-auto product-section">
            <div className="text-left mb-16">
              <h2 className="product-section-title">
                {siteConfig.categories.classic.slogan}
              </h2>
              <p className="product-section-subtitle">
                {siteConfig.categories.classic.description}
              </p>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
              {products.filter(p => p.category === 'classic').map(product => (
                <ProductCard 
                  key={product.id} 
                  product={product}
                  onShowCompatibility={setSelectedProduct}
                />
              ))}
            </div>
            <div className="text-center mt-12">
              <button 
                onClick={() => {
                  setSelectedCategory('classic');
                  setCurrentPage('products');
                }}
                className="text-brand-primary hover:text-brand-primary/80 text-lg font-medium underline"
              >
                View All Classic Series →
              </button>
            </div>
          </div>
        </section>

        {/* Accessories Section */}
        <section className="w-full">
          <div className="max-w-6xl mx-auto product-section">
            <div className="text-left mb-16">
              <h2 className="product-section-title">
                {siteConfig.categories.accessories.slogan}
              </h2>
              <p className="product-section-subtitle">
                {siteConfig.categories.accessories.description}
              </p>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
              {products.filter(p => p.category === 'accessories').map(product => (
                <ProductCard 
                  key={product.id} 
                  product={product}
                  onShowCompatibility={setSelectedProduct}
                />
              ))}
            </div>
            <div className="text-center mt-12">
              <button 
                onClick={() => {
                  setSelectedCategory('accessories');
                  setCurrentPage('products');
                }}
                className="text-brand-primary hover:text-brand-primary/80 text-lg font-medium underline"
              >
                View All Accessories →
              </button>
            </div>
          </div>
        </section>
      </div>
    </div>
  );

  const Products = () => (
    <div className="min-h-screen bg-brand-light">
      <div className="max-w-6xl mx-auto px-4 py-12">
        <h1 className="text-5xl font-light text-brand-primary text-center mb-12">
          {siteConfig.products.title}
        </h1>
        {/* Category Filter */}
        <div className="flex flex-wrap justify-center gap-4 mb-16">
          <button
            onClick={() => setSelectedCategory('all')}
            className={`px-8 py-3 rounded-full transition-all duration-300 font-medium ${
              selectedCategory === 'all' 
                ? 'bg-brand-primary text-white shadow-lg' 
                : 'bg-white text-brand-primary hover:bg-brand-secondary shadow-md hover:shadow-lg'
            }`}
          >
            {siteConfig.products.allProducts}
          </button>
          {orderedCategories.map(category => (
            <button
              key={category.id}
              onClick={() => setSelectedCategory(category.id)}
              className={`px-8 py-3 rounded-full transition-all duration-300 font-medium ${
                selectedCategory === category.id 
                  ? 'bg-brand-primary text-white shadow-lg' 
                  : 'bg-white text-brand-primary hover:bg-brand-secondary shadow-md hover:shadow-lg'
              }`}
            >
              {category.name}
            </button>
          ))}
        </div>

        {/* Product Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {filteredProducts.map(product => (
            <ProductCard 
              key={product.id} 
              product={product}
              onShowCompatibility={setSelectedProduct}
            />
          ))}
        </div>

        {/* Compatibility Modal */}
        {selectedProduct && (
          <CompatibilityModal
            product={selectedProduct}
            onClose={() => setSelectedProduct(null)}
          />
        )}

        {/* Silicone Info Modal */}
        {showSiliconeInfo && (
          <SiliconeInfoModal
            onClose={() => setShowSiliconeInfo(false)}
          />
        )}

        {/* Product Detail Modal */}
        {selectedProduct && (
          <ProductDetail
            product={selectedProduct}
            onClose={() => setSelectedProduct(null)}
          />
        )}

        {/* Feedback Modal */}
        <Feedback />
      </div>
    </div>
  );

  return (
    <Router>
      <div className="flex flex-col min-h-screen">
        <header className="fixed w-full z-50 bg-white shadow-md">
          <nav className="max-w-6xl mx-auto px-4 py-4 flex justify-between items-center">
            <div className="flex items-center">
              <Menu className="h-8 w-8 text-brand-primary cursor-pointer" onClick={() => setIsMenuOpen(true)} />
              <a href="/" className="ml-4 text-2xl font-bold text-brand-primary">
                Owala
              </a>
            </div>
            <div className="hidden md:flex items-center space-x-8">
              <a href="/" className="text-lg font-medium text-brand-primary hover:text-brand-secondary">
                Home
              </a>
              <a href="/products" className="text-lg font-medium text-brand-primary hover:text-brand-secondary">
                Products
              </a>
              <a href="/about" className="text-lg font-medium text-brand-primary hover:text-brand-secondary">
                About
              </a>
              <a href="/contact" className="text-lg font-medium text-brand-primary hover:text-brand-secondary">
                Contact
              </a>
              <a href="/blog" className="text-lg font-medium text-brand-primary hover:text-brand-secondary">
                Blog
              </a>
            </div>
            <div className="flex items-center space-x-4">
              <Search className="h-6 w-6 text-brand-primary cursor-pointer" />
              <ShoppingCart className="h-6 w-6 text-brand-primary cursor-pointer" />
              <Heart className="h-6 w-6 text-brand-primary cursor-pointer" />
              <X className="h-6 w-6 text-brand-primary cursor-pointer hidden md:block" onClick={() => setIsMenuOpen(false)} />
            </div>
          </nav>

          {/* Mobile Menu */}
          {isMenuOpen && (
            <div className="md:hidden fixed inset-0 bg-black bg-opacity-50 z-40">
              <div className="bg-white w-64 h-full p-6">
                <X className="h-6 w-6 text-brand-primary float-right" onClick={() => setIsMenuOpen(false)} />
                <div className="mt-12">
                  <a href="/" className="block text-lg font-medium text-brand-primary mb-6 hover:text-brand-secondary">
                    Home
                  </a>
                  <a href="/products" className="block text-lg font-medium text-brand-primary mb-6 hover:text-brand-secondary">
                    Products
                  </a>
                  <a href="/about" className="block text-lg font-medium text-brand-primary mb-6 hover:text-brand-secondary">
                    About
                  </a>
                  <a href="/contact" className="block text-lg font-medium text-brand-primary mb-6 hover:text-brand-secondary">
                    Contact
                  </a>
                  <a href="/blog" className="block text-lg font-medium text-brand-primary hover:text-brand-secondary">
                    Blog
                  </a>
                </div>
              </div>
            </div>
          )}
        </header>

        <main className="flex-grow pt-24">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/products" element={<Products />} />
            <Route path="/product/:id" element={<ProductDetail />} />
            <Route path="/about" element={<div className="min-h-screen flex items-center justify-center text-brand-primary">About Us</div>} />
            <Route path="/contact" element={<div className="min-h-screen flex items-center justify-center text-brand-primary">Contact Us</div>} />
            <Route path="/blog" element={<div className="min-h-screen flex items-center justify-center text-brand-primary">Blog</div>} />
          </Routes>
        </main>

        <Footer />
      </div>
    </Router>
  );
};

export default App;
